let sshot=document.querySelector(".screenshot-button"),img=document.querySelector("img"),vid=document.querySelector("#vid"),canva=document.querySelector("canvas"),gray=document.querySelector("#gray"),noSt=document.querySelector("#noSt"),cntex=document.querySelector("#cntrst"),bright=document.querySelector("#bright");window.onload=function(){navigator.mediaDevices.getUserMedia({video:!0}).then(stream=>{vid.srcObject=stream}).catch(err=>{console.log(err)})},noSt.onclick=function(){vid.style.cssText="filter: ;"},gray.onclick=function(){vid.style.cssText="filter: grayscale(1) contrast(1.5);"},cntex.onclick=function(){vid.style.cssText="filter: contrast(2.5);"},bright.onclick=function(){vid.style.cssText="filter: brightness(1.5);"},sshot.onclick=function(){canva.width=vid.videoWidth,canva.height=vid.videoHeight,console.log(canva),canva.getContext("2d").drawImage(vid,0,0);let dataUrl=canva.toDataURL("images/png");img.src=dataUrl;let hrefelem=document.createElement("a");hrefelem.href=dataUrl,document.body.append(hrefelem),hrefelem.download="screenshotelement$.png",hrefelem.click(),hrefelem.remove(),img.remove()};